import React, { Component } from "react";
import ProductDataService from "../services/product.service";

export default class AddProduct extends Component {
  constructor(props) {
    super(props);
    this.onChangeId = this.onChangeId.bind(this);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeOthername = this.onChangeOthername.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeSize = this.onChangeSize.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onChangeDescription2 = this.onChangeDescription2.bind(this);
    this.onChangeWater = this.onChangeWater.bind(this);
    this.onChangeLight = this.onChangeLight.bind(this);
    this.uploadPicture = this.uploadPicture.bind(this);
    this.onChangeFriendly = this.onChangeFriendly.bind(this);
    this.saveProduct = this.saveProduct.bind(this);
    this.newProduct = this.newProduct.bind(this);

    this.state = {
      id: null,
      p_name: "",
      p_othername: "", 
      p_price: 20,
      p_desc: "",
      p_desc2: "",  
      p_water: "",
      p_light: "",
      p_pet: false,
      imagename: "", 
      submitted: false
    };
  }

  onChangeId(e) {
    this.setState({
      id: e.target.value
    });
  }

  onChangeName(e) {
    this.setState({
      name: e.target.value
    });
  }

  onChangeOthername(e) {
    this.setState({
      othername: e.target.value
    });
  }

  onChangePrice(e) {
    this.setState({
      p_price: e.target.value
    });
  }

  onChangeSize(e) {
    this.setState({
      size: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      p_desc: e.target.value
    });
  }

  onChangeDescription2(e) {
    this.setState({
      p_desc2: e.target.value
    });
  }

  onChangeWater(e) {
    this.setState({
      p_water: e.target.value
    });
  }

  onChangeLight(e) {
    this.setState({
      p_light: e.target.value
    });
  }

  onChangeFriendly(e) {
    this.setState({
      p_pet: e.target.value
    });
  }

  uploadPicture(e) {
    this.setState({
      picturePreview : URL.createObjectURL(e.target.files[0]),
      pictureAsFile : e.target.files[0]
    });
  }

  saveProduct() {
    var data = {
      id: this.state.id,
      p_name: this.state.p_name,
      p_othername: this.state.p_othername,
      p_price: this.state.p_price,
      p_size: this.state.p_size,
      p_desc: this.state.p_desc,
      p_desc2: this.state.p_desc2,
      p_water: this.state.p_water,
      p_light: this.state.p_light,
      p_pet: this.state.p_pet,
      imagename: this.state.imagename
    };

    ProductDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          name: response.data.name,
          price: response.data.price,
          description: response.data.description,
          water: response.data.water,
          light: response.data.light,
          pet: response.data.pet,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newProduct() {
    this.setState({
      id: null,
      name: "",
      price: 20,
      description: "",
      water: "",
      light: "",
      pet: false,

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newProduct}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="id">id</label>
              <input
                type="number"
                className="form-control"
                id="id"
                required
                value={this.state.id}
                onChange={this.onChangeId}
                name="id"
              />
            </div>
            <div className="form-group">
              <label htmlFor="name">Plant Name</label>
              <input
                type="text"
                className="form-control"
                id="name"
                required
                value={this.state.name}
                onChange={this.onChangeName}
                name="name"
              />
            </div>


            <div className="form-group">
              <label htmlFor="price">Price</label>
              <input
                type="number"
                className="form-control"
                id="price"
                step="5"
                required
                value={this.state.price}
                onChange={this.onChangePrice}
                name="price"
              />
            </div>

            <div className="form-group">
              <label htmlFor="description">Description</label>
              <input
                type="text"
                className="form-control"
                id="description"
                required
                value={this.state.description}
                onChange={this.onChangeDescription}
                name="description"
              />
            </div>

            <div className="form-group">
              <label htmlFor="water">Water</label>
              <input
                type="text"
                className="form-control"
                id="water"
                required
                value={this.state.water}
                onChange={this.onChangeWater}
                name="water"
              />
            </div>

            <div className="form-group">
              <label htmlFor="light">Light</label>
              <input
                type="text"
                className="form-control"
                id="light"
                required
                value={this.state.light}
                onChange={this.onChangeLight}
                name="light"
              />
            </div>

            <div className="form-group">
              <label htmlFor="pet">Pet & Children Friendly?</label>
              <input
                type="radio"
                className="form-control"
                id="pet"
                step="5"
                required
                value={this.state.pet}
                onChange={this.onChangeFriendly}
                name="pet"
              />
            </div>
            <div className="form-group">
              <label htmlFor="image">Product Image</label>
              <input
                type="file"
                className="form-control"
                onChange={this.uploadPicture}
                name="image"
              />
            </div>
            <button onClick={this.saveProduct} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
